var mousePos = { x: 0, y: 0  };
$(document).mousemove(function(event) {
    mousePos.x = event.pageX;
    mousePos.y = event.pageY;
    console.log(mousePos);
    inside = isInsideTarget();
    console.log(inside);
    if (inside) {
        beep();
        updateTargetPos();
    }
});

var getEucledianDist = function (pt1, pt2) {
    var x = pt1.x - pt2.x,
    y = pt1.y - pt2.y, 
    dist = Math.sqrt( x*x + y*y );
    return dist;
}

var isInsideTarget = function () {
    var target = $('#target'),
    pos = target.position(),
    top = pos.top,
    left = pos.left,
    width = target.width(),
    height = target.height(),
    hCenter = top + height/2,
    wCenter = left + width/2,
    center = { x: wCenter, y: hCenter },
    dist = getEucledianDist(center, mousePos);
    if (dist < width/2) {   //Assuming height = width
        return true;
    } else {
        return false;
    }
};

var getRandomInt = function (max) {
  return Math.floor(Math.random() * Math.floor(max));
};

var updateTargetPos = function () {
    var target = $('#target'),
    pos = target.position(),
    top = pos.top,
    left = pos.left,
    width = target.width(),
    height = target.height(),
    hCenter = top + height/2,
    wCenter = left + width/2,
    pt = { x: wCenter, y: hCenter };

    var windowWidth = window.innerWidth,
    windowHeight = window.innerHeight,
    newLeft = getRandomInt(windowWidth - width),
    newTop = getRandomInt(windowHeight - height);
    newPt = { x: newLeft + width/2, y: newTop + height/2 };
    while (getEucledianDist(pt, newPt) < width) { //assuming height = width
        newLeft = getRandomInt(windowWidth - width),
        newTop = getRandomInt(windowHeight - height);
        newPt = { x: newLeft + width/2, y: newTop + height/2 };
    }
    target.css({ top: newTop, left: newLeft });
};


$(document).ready(function () {
    var dots = [];
    
    // The Dot object used to scaffold the dots
    var Dot = function() {
        this.x = 0;
        this.y = 0;
        this.node = (function(){
            var n = document.createElement("div");
            n.className = "trail";
            document.body.appendChild(n);
            return n;
        }());
    };
    // The Dot.prototype.draw() method sets the position of 
      // the object's <div> node
    Dot.prototype.draw = function() {
      this.node.style.left = this.x + "px";
      this.node.style.top = this.y + "px";
    
    };
    
    // Creates the Dot objects, populates the dots array
    for (var i = 0; i < 12; i++) {
      var d = new Dot();
      dots.push(d);
    }
    
    // This is the screen redraw function
    function draw() {
      // Make sure the mouse position is set everytime
        // draw() is called.
      var x = mousePos.x,
          y = mousePos.y;
      
      // This loop is where all the 90s magic happens
      dots.forEach(function(dot, index, dots) {
        var nextDot = dots[index + 1] || dots[0];
        
        dot.x = x;
        dot.y = y;
        dot.draw();
        x += (nextDot.x - dot.x) * .6;
        y += (nextDot.y - dot.y) * .6;
        });
    }
    // animate() calls draw() then recursively calls itself
      // everytime the screen repaints via requestAnimationFrame().
    function animate() {
      draw();
      requestAnimationFrame(animate);
    }
    
    // And get it started by calling animate().
    animate();

});

// dots is an array of Dot objects,
// mouse is an object used to track the X and Y position
   // of the mouse, set with a mousemove event listener below

function beep() {
    var snd = new Audio("data:audio/wav;base64,");  
    snd.play();
}

